import Header from "./Header.vue";
import Footer from "./Footer.vue"; // import loading from '../components/loading.vue';

export default {
  name: 'App',
  components: {
    Header: Header,
    Footer: Footer
  },
  watch: {
    $route: {
      //路由监听，属性检测
      handler: function handler(nextValue, PrevValue) {
        // console.log('路由变化了',nextValue);
        var path = nextValue.path;

        if (/home|follow|column/.test(path)) {
          //home follow column
          // this.$root.bNav=this.$root.bFoot=true;
          this.$store.commit('UPDATE_NAV', true);
          this.$store.commit('UPDATE_FOOT', true);
        }

        if (/detail|login|reg/.test(path)) {
          //detial login reg
          // this.$root.bNav=this.$root.bFoot=false;
          this.$store.commit('UPDATE_NAV', false);
          this.$store.commit('UPDATE_FOOT', false);
        }

        if (path.includes('/user')) {
          //user
          // this.$root.bNav=false;
          // this.$root.bFoot=true;
          this.$store.commit('UPDATE_NAV', false);
          this.$store.commit('UPDATE_FOOT', true);
        }
      },
      immediate: true
    }
  }
};
